mapper.select:
select errd.id, err.code, err.state, err.related_order, errd.record_time, errd.num,
epo.company_name, epo.supply_name, ep.name productName
from erp_repo_receipt_detail errd
left join erp_repository_receipt err on err.id = errd.receipt_id and err.deleted = 0
left join erp_purchase_order epo on epo.id = err.related_order_id and epo.deleted = 0
left join erp_product ep on ep.id = errd.product_id and ep.deleted = 0
where errd.deleted = 0 and err.type = 'material_stock'

    and err.org_id = #{companyId}


    and err.related_order like CONCAT('%',#{relatedOrder},'%')


    and epo.supply_name like CONCAT('%',#{supplyName},'%')


    and ep.name like CONCAT('%',#{productName},'%')


    and errd.record_time >= #{receiptStartDate}


    and errd.record_time <= #{receiptEndDate}


    and err.state = #{state}


    and err.code = #{code}


    and err.org_id in (

         #{id}

    )

order by errd.record_time desc

select errd.id, err.code, err.state, err.related_order, errd.record_time, errd.num,
eso.company_name, ep.name productName, ac.cus_name customName
from erp_repo_receipt_detail errd
left join  erp_repository_receipt err on err.id = errd.receipt_id and err.deleted = 0
left join erp_sale_order eso on eso.id = err.related_order_id and eso.deleted = 0
left join erp_product ep on ep.id = errd.product_id and ep.deleted = 0
left join auth_customer ac on ac.id = eso.customer and ac.deleted = 0
where errd.deleted = 0 and err.type = 'product_delivery'

    and err.org_id = #{companyId}


    and err.related_order like CONCAT('%',#{relatedOrder},'%')


    and ep.name like CONCAT('%',#{productName},'%')


    and errd.record_time >= #{receiptStartDate}


    and errd.record_time <= #{receiptEndDate}


    and err.state = #{state}


    and err.code = #{code}


    and err.org_id in (

        #{id}

    )

order by errd.record_time desc

choose.when:
SELECT c.`name` AS productName, d.num AS receiptNum, r.`name` AS storeHouseName, m.state, p.supply_name AS supplyName, m.receipt_date AS receiptDate, m.`code` AS warehousingOder, p.contract_no AS purchasingOder, e.`name` AS companyName FROM erp_repo_receipt_detail d LEFT JOIN erp_repository_receipt m ON d.receipt_id = m.id LEFT JOIN erp_purchase_order p ON m.related_order_id = p.id LEFT JOIN auth_enterprise e ON p.company = e.id LEFT JOIN erp_repository r ON d.repository_id = r.id LEFT JOIN erp_product c ON d.product_id = c.id WHERE 1 = 1 AND d.deleted=0 AND m.deleted=0 AND m.type = 'material_stock'
AND p.company IN
#{item}
AND p.company =#{companyId}
AND r.`name` LIKE CONCAT('%',#{storeHouseName} ,'%')
AND c.`name` LIKE CONCAT('%',#{productName},'%')
AND m.related_order LIKE CONCAT('%',#{sourceOrder},'%')
AND m.receipt_date >= #{startTime}
AND m.receipt_date <= #{endTime}
ORDER BY m.receipt_date DESC

SELECT e.`name` AS companyName, b.material_batch_name AS materialBatchName, m.CODE AS outOfStockOrder, p.`name` AS productName, m.out_of_stock_time AS outOfStockDate, f.`name` AS furnaceNumber, m.classes AS workShift, r.`name` AS storeHouseName, d.number AS outOfStockNum FROM erp_out_of_stock_order_detail d LEFT JOIN erp_out_of_stock_order m ON d.out_of_stock_order_id = m.id LEFT JOIN erp_product p ON d.product_id = p.id LEFT JOIN erp_furnace f ON m.furnace_number_id = f.id LEFT JOIN auth_enterprise e ON m.company_id = e.id LEFT JOIN erp_material_batch_order b ON m.material_batch_id = b.id LEFT JOIN erp_repository r ON d.repository_id = r.id WHERE 1 = 1 AND d.deleted =0 AND m.deleted =0
AND m.company_id IN
#{item}
AND m.company_id =#{companyId}
AND r.`name` LIKE CONCAT('%',#{storeHouseName} ,'%')
AND p.`name` LIKE CONCAT('%',#{productName},'%')
AND b.material_batch_name LIKE CONCAT('%',#{sourceOrder},'%')
AND m.out_of_stock_time >= #{startTime}
AND m.out_of_stock_time <= #{endTime}
ORDER BY m.out_of_stock_time DESC

SELECT e.`name` AS companyName, b.material_batch_name AS materialBatchName, w.`code` AS warehousingOder, w.warehousing_time AS receiptDate, r.`name` AS storeHouseName, f.`name` AS furnaceNumber, w.classes AS workShift, w.amount AS warehousingNum
FROM erp_warehousing_order w LEFT JOIN auth_enterprise e ON w.company_id = e.id LEFT JOIN erp_material_batch_order b ON w.material_batch_id = b.id LEFT JOIN erp_repository r ON w.repository_id = r.id LEFT JOIN erp_furnace f ON w.furnace_id = f.id WHERE 1 = 1 AND w.deleted =0
AND w.company_id IN
#{item}
AND w.company_id =#{companyId}
AND r.`name` LIKE CONCAT('%',#{storeHouseName} ,'%')
AND b.material_batch_name LIKE CONCAT('%',#{sourceOrder},'%')
AND w.warehousing_time >= #{startTime}
AND w.warehousing_time <= #{endTime}
ORDER BY w.warehousing_time DESC

SELECT c.`name` AS productName, r.`name` AS storeHouseName, m.state, e.`name` AS companyName, a.cus_name AS customerName , d.num AS sendNum , m.code AS outOfStockOrder , m.receipt_date AS outOfStockDate , s.contract_no AS saleOrder FROM erp_repo_receipt_detail d LEFT JOIN erp_repository_receipt m ON d.receipt_id = m.id LEFT JOIN erp_sale_order s ON m.related_order_id = s.id LEFT JOIN auth_enterprise e ON s.company = e.id LEFT JOIN erp_repository r ON d.repository_id = r.id LEFT JOIN erp_product c ON d.product_id = c.id LEFT JOIN auth_customer a ON s.customer = a.id WHERE 1 = 1 AND d.deleted=0 AND m.deleted=0 AND m.type = 'product_delivery'
AND s.company IN
#{item}
AND s.company =#{companyId}
AND r.`name` LIKE CONCAT('%',#{storeHouseName} ,'%')
AND c.`name` LIKE CONCAT('%',#{productName},'%')
AND m.related_order LIKE CONCAT('%',#{sourceOrder},'%')
AND m.receipt_date >= #{startTime}
AND m.receipt_date <= #{endTime}
ORDER BY m.receipt_date DESC

mapper.select:
SELECT m.id, e.`name` AS companyName, p.contract_no AS purchaseCode, m.`code` AS inCode, m.receipt_date AS receiptDate, c.cus_name AS supplier, pd.`name` AS productName, sum( d.num ) AS num, m.state FROM erp_repository_receipt m LEFT JOIN auth_enterprise e ON m.org_id = e.id LEFT JOIN erp_purchase_order p ON m.related_order_id = p.id LEFT JOIN auth_customer c ON p.supplier = c.id LEFT JOIN erp_repo_receipt_detail d ON d.receipt_id = m.id LEFT JOIN erp_product pd ON d.product_id = pd.id WHERE 1 = 1 AND m.deleted = 0 AND ( e.deleted = 0 OR e.id IS NULL ) AND ( e.status = 1 OR e.id IS NULL ) AND ( p.deleted = 0 OR p.id IS NULL ) AND ( d.deleted = 0 OR d.id IS NULL ) AND ( pd.deleted = 0 OR pd.id IS NULL ) AND ( pd.status = 1 OR pd.id IS NULL ) AND m.type = 'material_stock'
AND m.org_id IN
#{item}
AND m.org_id =#{companyId}
AND p.contract_no LIKE CONCAT('%',#{purchaseCode},'%')
AND p.supply_name LIKE CONCAT('%',#{supplier},'%')
AND pd.`name` LIKE CONCAT('%',#{productName},'%')
AND m.`code` LIKE CONCAT('%',#{inCode},'%')
AND m.receipt_date >= #{startTime}
AND m.receipt_date <= #{endTime}
GROUP BY m.id , d.product_id ORDER BY m.state ASC, m.receipt_date DESC

SELECT m.id, e.`name` AS companyName, s.contract_no AS saleCode, m.`code` AS outCode, m.receipt_date AS receiptDate, c.cus_name AS customer, p.`name` AS productName, sum( d.num ) AS num, m.state FROM erp_repository_receipt m LEFT JOIN auth_enterprise e ON m.org_id = e.id LEFT JOIN erp_sale_order s ON m.related_order_id = s.id LEFT JOIN auth_customer c ON s.customer = c.id LEFT JOIN erp_repo_receipt_detail d ON d.receipt_id = m.id LEFT JOIN erp_product p ON d.product_id = p.id WHERE 1 = 1 AND m.deleted = 0 AND ( e.deleted = 0 OR e.id IS NULL ) AND ( e.status = 1 OR e.id IS NULL ) AND ( s.deleted = 0 OR s.id IS NULL ) AND ( d.deleted = 0 OR d.id IS NULL ) AND ( p.deleted = 0 OR p.id IS NULL ) AND ( p.status = 1 OR p.id IS NULL ) AND m.type = 'product_delivery'
AND m.org_id IN
#{item}
AND m.org_id =#{companyId}
AND s.contract_no LIKE CONCAT('%',#{saleCode},'%')
AND s.customer_name LIKE CONCAT('%',#{customer},'%')
AND d.product_id =#{productId}
AND m.`code` LIKE CONCAT('%',#{outCode},'%')
AND m.receipt_date >= #{startTime}
AND m.receipt_date <= #{endTime}
GROUP BY m.id , d.product_id ORDER BY m.state ASC, m.receipt_date DESC
